mapper.select:



		AND menu_key=#{menuKey}


		AND menu_name like  '%'||#{menuName}||'%'


		AND visible = #{visible}


		AND menu_type=#{menuType}

		AND parent_id=#{parentId}


	and delete_flag='0'

order by order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, menu_type = #{menuType}, target = #{target}, menu_key = #{menuKey}, component = #{component}, visible = #{visible}, perms = #{perms}, icon = #{icon}, path = #{path}, redirect = #{redirect}, hidden_children = #{hiddenChildren}, hidden_header = #{hiddenHeader}, remark = #{remark}, update_by = #{updateBy}, delete_flag = #{deleteFlag}, update_time = now()
where menu_id = #{menuId}

mapper.select:


	AND menu_type != 'F'

	AND parent_id=#{parentId}


order by order_num

SELECT a.menu_id as menuId, a.menu_name as menuName, a.parent_id as parentId, (case a.parent_id WHEN 0 then 'æ— ' else b.menu_name end )as parentName , a.target , a.order_num as orderNum, a.menu_type as menuType, a.menu_key as menuKey, a.component as component, a.visible as visible, COALESCE ( a.perms, '' ) AS perms, a.icon, a.PATH as path, a.redirect, a.hidden_children, a.hidden_header, a.create_by, a.create_time, a.delete_flag as deleteFlag FROM sys_menu a left join sys_menu b on a.parent_id=b.menu_id
and a.delete_flag='0'
AND menu_key=#{menuKey}
AND menu_name like '%'||#{menuName}||'%'
AND visible = #{visible}
AND menu_type=#{menuType}
AND parent_id=#{parentId}
ORDER BY a.menu_id
